import React from 'react';
import logo from "./logo.svg";
import "./App.css";
import {
  InfoWindow,
  withScriptjs,
  withGoogleMap,
  GoogleMap,
  Marker,
} from "react-google-maps";
import Geocode from "react-geocode"
Geocode.setApiKey

class App extends React.Component{

    state = {
      address:"",
      city:"",
      area: "",
      state: "",
      zoom: 15,
      height: 400,
      mapPosition: {
        lat: 0,
        lng: 0,
      },
      markerPosition:{
        lat:0,
        lng: 0,
      }
    }  

    onMarkerDragEnd = (event) => {
      let newLat = event.latLng.lat(),
          newLng = event.latLng.lng();
          console.log("newLat =", newLat, "newLng =", newLng)
    }
  render() {
    const MapWithAMarker = withScriptjs(withGoogleMap(props =>
      <GoogleMap
        defaultZoom={10}
        defaultCenter={{ lat: 44.986656, lng: -93.258133 }}
      >
        <Marker
        draggable ={true}
        onDragEnd ={this.onMarkerDragEnd}
          position={{ lat: 44.986656, lng: -93.258133 }}
        >
          <InfoWindow>
            <div>
              Name:<br/>
              Description: <br/>
              Quantity:
            </div>
          </InfoWindow>
        </Marker>
      </GoogleMap>
    ));
  
    return (
        
    <MapWithAMarker
      googleMapURL="https://maps.googleapis.com/maps/api/js?key=AIzaSyD812o98-5qpcViO3kCoUa8mpd4eyflbPo&v=3.exp&libraries=geometry,drawing,places"
      loadingElement={<div style={{ height: `100%` }} />}
      containerElement={<div style={{ height: `400px` }} />}
      mapElement={<div style={{ height: `100%` }} />}
    />
   
  );
  }
  
  
}

export default App;
